// src/components/utils/ProjectCard.jsx
import React from "react";
import PropTypes from "prop-types";

const ProjectCard = ({
  project_title,
  project_description,
  project_url,
  tech_used,
  youtube_video_id,
  project_image,
}) => {
  const youtubeThumbnailUrl = `https://img.youtube.com/vi/${youtube_video_id}/0.jpg`;

  return (
    <div className="mb-8">
      <h3 className="text-xl font-bold text-slate-200">{project_title}</h3>
      <div className="flex flex-col md:flex-row">
        <a href={`https://www.youtube.com/watch?v=${youtube_video_id}`} target="_blank" rel="noopener noreferrer">
          <img
            src={youtubeThumbnailUrl}
            alt={`${project_title} thumbnail`}
            className="w-full md:w-1/3 h-auto"
          />
        </a>
        <div className="md:ml-4">
          <ul className="list-disc list-inside text-gray-500 dark:text-gray-400">
            {project_description.map((desc, index) => (
              <li key={index}>{desc}</li>
            ))}
          </ul>
          <div className="mt-2">
            <strong>Technologies used:</strong>
            <ul className="list-disc list-inside text-gray-500 dark:text-gray-400">
              {tech_used.map((tech, index) => (
                <li key={index}>{tech}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

ProjectCard.propTypes = {
  project_title: PropTypes.string.isRequired,
  project_description: PropTypes.arrayOf(PropTypes.string).isRequired,
  project_url: PropTypes.string.isRequired,
  tech_used: PropTypes.arrayOf(PropTypes.string).isRequired,
  youtube_video_id: PropTypes.string.isRequired,
  project_image: PropTypes.string,
};

export default ProjectCard;
